import React, { useContext } from "react";
import { GameContext } from "../../GameContext";

import { Button } from "reactstrap";

const AppendAction = ({
  haveTurn,
  gameState,
  currentResult,
  isPossible,
  getNextResult,
  number
}) => {
  return (
    <Button
      className={"append"}
      disabled={!isPossible(currentResult) || gameState !== "ready"}
      onClick={() => haveTurn(getNextResult)}
      title="Append digit to end - Apending 2 to 21 = 212"
    >
      {number}
    </Button>
  );
};
const AppendActionContainer = props => {
  const number = Math.floor(Math.random() * 9) + 1;

  const getNextResult = result =>
    parseInt(result.toString() + number.toString());

  const isPossible = currentValue => true;

  return {
    getNextResult: getNextResult,
    hash: "append-" + number,
    possible: () => {
      let { targetResult } = useContext(GameContext);
      return isPossible(targetResult);
    },

    button: (
      <GameContext.Consumer>
        {({ haveTurn, gameState, currentResult, isPossible, number }) => <AppendAction  />}
      </GameContext.Consumer>
    )
  };
};

export default AppendActionContainer;
