export const ReverseAction = () => {
  const getNextResult = result => {
    return parseInt(
      (result < 0 ? "-" : "") +
        [...result.toString()]
          .filter(x => x.toString() !== "-")
          .reverse()
          .join("")
    );
  };
  const isPossible = currentValue => currentValue.toString().length > 1;

  return {
    getNextResult: getNextResult,
    hash: "reverse",
    possible: isPossible,

    button: (
      <GameContext.Consumer key={"reverse"}>
        {({ haveTurn, gameState, currentResult }) => (
          <Button
            className={"reverse"}
            disabled={!isPossible(currentResult) || gameState !== "ready"}
            onClick={() => haveTurn(getNextResult)}
            title="Reverse numbers 1,2,3 = 3,2,1"
          >
            &lt;-&gt;
          </Button>
        )}
      </GameContext.Consumer>
    )
  };
};

export const AppendAction = () => {

  let number = Math.floor(Math.random() * 9) + 1

  const getNextResult = result =>
    parseInt(result.toString() + number.toString());

    const isPossible = currentValue => true;

  return {
    getNextResult: getNextResult,
    hash: "append-" + number,
    possible: () => {
      let { targetResult } = useContext(GameContext)
      return isPossible(targetResult)
    },

    button: (
      <GameContext.Consumer key={"append-" + number}>
        {({ haveTurn, gameState, currentResult }) => (
          <Button
            className={"append"}
            disabled={!isPossible(currentResult) || gameState !== "ready"}
            onClick={() => haveTurn(getNextResult)}
            title="Append digit to end - Apending 2 to 21 = 212"
          >
            {number}
          </Button>
        )}
      </GameContext.Consumer>
    )
  };
};

export const SubtractAction = () => {
  let number = Math.floor(Math.random() * 9) + 1;

  const getNextResult = result => result - number;
  const isPossible = currentValue => true;
  return {
    getNextResult: getNextResult,
    hash: "subtract-" + number,
    possible: isPossible,

    button: (
      <GameContext.Consumer key={"subtract-" + number}>
        {({ haveTurn, gameState, currentResult }) => (
          <Button
            className={"subtract"}
            title={"Subtract " + number}
            disabled={!isPossible(currentResult) || gameState !== "ready"}
            onClick={() => haveTurn(getNextResult)}
          >
            - {number}
          </Button>
        )}
      </GameContext.Consumer>
    )
  };
};

export const AddAction = () => {
  let number = Math.floor(Math.random() * 9) + 1;

  const getNextResult = result => result + number;
  const isPossible = currentValue => true;

  return {
    getNextResult: getNextResult,
    hash: "add-" + number,
    possible: isPossible,

    button: (
      <GameContext.Consumer key={"add-" + number}>
        {({ haveTurn, gameState, currentResult }) => (
          <Button
            className={"add"}
            title={"Add " + number}
            disabled={!isPossible(currentResult) || gameState !== "ready"}
            onClick={() => haveTurn(getNextResult)}
          >
            + {number}
          </Button>
        )}
      </GameContext.Consumer>
    )
  };
};
export const MultiplyAction = () => {
  let number = Math.floor(Math.random() * 3) + 1;

  const getNextResult = result => result * number;
  const isPossible = currentValue => true;

  return {
    getNextResult: getNextResult,
    hash: "multiply-" + number,
    possible: isPossible,

    button: (
      <GameContext.Consumer key={"multiply-" + number}>
        {({ haveTurn, gameState, currentResult }) => (
          <Button
            className={"multiply"}
            title={"Multiply " + number}
            disabled={!isPossible(currentResult) || gameState !== "ready"}
            onClick={() => haveTurn(getNextResult)}
          >
            X {number}
          </Button>
        )}
      </GameContext.Consumer>
    )
  };
};
export const DivideAction = () => {
  let number = Math.floor(Math.random() * 3) + 1;

  const { targetResult } = useContext(GameContext)

  const getNextResult = result => result / number;
  const isPossible = currentValue => currentValue > 1 && (currentValue % number == 0)   ;
  const isPossibleForTarget = () => targetResult > 1 && (currentValue % number == 0)   ;

  return {
    getNextResult: getNextResult,
    hash: "divide-" + number,
    possible: isPossibleForTarget,

    button: (
      <GameContext.Consumer key={"divide-" + number}>
        {({ haveTurn, gameState, currentResult }) => (
          <Button
            className={"divide"}
            title={"Divide " + number}
            disabled={!isPossible(currentResult) || gameState !== "ready"}
            onClick={() => haveTurn(getNextResult)}
          >
            / {number}
          </Button>
        )}
      </GameContext.Consumer>
    )
  };
};

export default Buttons;
