import React, { useState } from "react";
import { GameContext } from "../../GameContext";

import { Button } from "reactstrap";

import "bootstrap/dist/css/bootstrap.css";

import "../GameBoard.css";

const ReverseAction = ({
  haveTurn,
  gameState,
  currentResult,
  isPossible,
  getNextResult,
  number
}) => {
  return (
    <Button
      className={"reverse"}
      disabled={!isPossible(currentResult) || gameState !== "ready"}
      onClick={() => haveTurn(getNextResult)}
        title="Reverse numbers 123 = 321"
>
      &lt;-^gt;
    </Button>
  );
};
const ReverseActionContainer = props => {
  const number =Math.floor(Math.random() * 3) + 1;

  const getNextResult = (result) => parseInt(
          (result < 0 ? "-" : "") +
            [...result.toString()]
              .filter(x => x.toString() !== "-")
              .reverse()
              .join(""))


              const isPossible = value => value.toString().length > 1


  return {
    getNextResult: getNextResult,
    hash: "reverse-" + number,

    possible: (value) => {
      return isPossible(value);
    },

    button: (
      <GameContext.Consumer>
        {({ haveTurn, gameState, currentResult }) => <ReverseAction currentResult={currentResult} haveTurn={haveTurn} gameState={gameState} number={number} isPossible={isPossible} getNextResult={getNextResult} />}
      </GameContext.Consumer>
    )
  };
};

export default ReverseActionContainer;
