import React, { useContext } from "react";
import { GameContext } from "../../GameContext";

import { Row, Col, Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.css";

import "../GameBoard.css";
import { targetPropType } from "reactstrap/lib/utils";

function Buttons() {
  return (
    <GameContext.Consumer>
      {({ buttons, gameState, resetGame }) => (
        <Row className={"buttons"}>
          <Col className={"game-button"} xs={4} />
          <Col className={"game-button"} xs={4}>
            {buttons[0] && buttons[0].button}
          </Col>
          <Col className={"game-button"} xs={4}>
            <Button onClick={resetGame}>CLR</Button>
          </Col>

          <Col className={"game-button"} xs={4} />
          <Col className={"game-button"} xs={4}>
            {buttons[1] && buttons[1].button}
          </Col>
          <Col className={"game-button"} xs={4}>
            {buttons[2] && buttons[2].button}
          </Col>

          <Col className={"game-button"} xs={4} />
          <Col className={"game-button"} xs={4}>
            {buttons[3] && buttons[3].button}
          </Col>
          <Col className={"game-button"} xs={4}>
            {buttons[4] && buttons[4].button}
          </Col>
        </Row>
      )}
    </GameContext.Consumer>
  );
}
export default Buttons;
